def find_path (source_point, destination_point, mesh):    """    Searches for a path from source_point to destination_point through the mesh    Args:        source_point: starting point of the pathfinder        destination_point: the ultimate goal the pathfinder must reach        mesh: pathway constraints the path adheres to    Returns:        A path (list of points) from source_point to destination_point if exists        A list of boxes explored by the algorithm    """    path = []    boxes = {}    boxes_sour = []    boxes_dest = []    print('source_point: ',source_point)    print('destination_point', destination_point)    #Check intial boxes    for box in mesh['boxes']:        #Checking source y's        if(source_point[0] > box[2] and source_point[0] < box[3]):            #Checking source x's            if(source_point[1] > box[0] and source_point[1] < box[1]):                #Add source box                boxes_sour.append(box)                print('source_box: ', box)                print(boxes_sour)                #Checking destination y's                if(destination_point[0] > box[2] and destination_point[0] < box[3]):                    #Cheching destiantion x's                    if(destination_point[1] > box[0] and destination_point[1] < box[1]):                        #Add destiantion box                        boxes_dest.append(box)                        print('destination_box: ', box)                        print(boxes_dest)                        break        #Checking destination y's        if(destination_point[0] > box[2] and destination_point[0] < box[3]):            #Checking destiantion x's            if(destination_point[1] > box[0] and destination_point[1] < box[1]):                #Add destination box                boxes_dest.append(box)                print('destination_box: ', box)                print(boxes_dest)    if(boxes_dest and boxes_sour):        if(boxes_dest[0] == boxes_sour[0]):            print('Done')    return path, boxes.keys()